import { expect, test } from 'vitest';

import { teamNameToTeamSlug } from '../utils/team.js';

test('teamNameToTeamSlug', () => {
  expect(teamNameToTeamSlug('Test Team')).toBe('test-team');
  expect(teamNameToTeamSlug('         Test Team       ')).toBe('test-team');
  expect(teamNameToTeamSlug('Test Team 1123')).toBe('test-team');
  expect(teamNameToTeamSlug('Test Team +ČĚŠŘĚÍŽÝĚŠÁŘŽĚŠŘČ+')).toBe('test-team');
  expect(teamNameToTeamSlug('Můj První Tým')).toBe('mj-prvn-tm');
});
