import TOML from '@ltd/j-toml';

export const toml = {
  parse: (text: string): ReturnType<typeof TOML.parse> =>
    TOML.parse(text, { x: { null: true }, bigint: Number.MAX_SAFE_INTEGER }),
  stringify: (value: any, opts?: Parameters<typeof TOML.stringify>[1]): string =>
    TOML.stringify(value, { ...opts, ...{ newline: '\n', xNull: true }, integer: Number.MAX_SAFE_INTEGER }),
};
